/**
 * Extracts cart_id from ANY possible structure:
 * - cart_id key (any depth)
 * - stringified JSON
 * - arrays
 * - flat objects
 * - raw UUID dumped under any key
 */

// UUID v4 pattern (safe + strict)
const UUID_REGEX =
  /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

function extractCartId(data) {
  let foundCartId = null;

  function traverse(value) {
    if (foundCartId !== null) return;

    // Case 1: String
    if (typeof value === 'string') {
      // Try JSON parse
      try {
        const parsed = JSON.parse(value);
        traverse(parsed);
        return;
      } catch (e) {
        // Not JSON → check if UUID
        if (UUID_REGEX.test(value)) {
          foundCartId = value;
        }
        return;
      }
    }

    // Case 2: Array
    if (Array.isArray(value)) {
      for (const item of value) {
        traverse(item);
        if (foundCartId !== null) return;
      }
      return;
    }

    // Case 3: Object
    if (typeof value === 'object' && value !== null) {
      for (const key of Object.keys(value)) {
        // Highest priority: explicit cart_id
        if (key === 'cart_id') {
          foundCartId = value[key];
          return;
        }
        traverse(value[key]);
        if (foundCartId !== null) return;
      }
    }
  }

  traverse(data);
  return foundCartId;
}

// n8n input (single or multiple items)
const inputData = $input.all();

const cartId = extractCartId(inputData);

return [
  {
    cart_id: cartId
  }
];
