/**
 * Universal extractor for:
 * - cart_id
 * - product_id
 * - quantity
 *
 * Handles:
 * - Deep nesting
 * - Arrays
 * - Flat objects
 * - Stringified JSON
 * - Raw UUIDs / numbers
 */

// UUID v4 pattern
const UUID_REGEX =
  /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

function extractFields(data) {
  const result = {
    cart_id: null,
    product_id: null,
    quantity: null,
  };

  function traverse(value) {
    if (
      result.cart_id !== null &&
      result.product_id !== null &&
      result.quantity !== null
    ) {
      return;
    }

    // Case 1: String
    if (typeof value === 'string') {
      // Try JSON
      try {
        const parsed = JSON.parse(value);
        traverse(parsed);
        return;
      } catch (e) {
        // UUID fallback
        if (UUID_REGEX.test(value)) {
          if (result.cart_id === null) {
            result.cart_id = value;
          } else if (result.product_id === null) {
            result.product_id = value;
          }
        }
        return;
      }
    }

    // Case 2: Number â†’ quantity fallback
    if (typeof value === 'number') {
      if (result.quantity === null) {
        result.quantity = value;
      }
      return;
    }

    // Case 3: Array
    if (Array.isArray(value)) {
      for (const item of value) {
        traverse(item);
      }
      return;
    }

    // Case 4: Object
    if (typeof value === 'object' && value !== null) {
      for (const key of Object.keys(value)) {
        const val = value[key];

        // Explicit keys always win
        if (key === 'cart_id') {
          result.cart_id = val;
        } else if (key === 'product_id') {
          result.product_id = val;
        } else if (key === 'quantity') {
          result.quantity = val;
        } else {
          traverse(val);
        }
      }
    }
  }

  traverse(data);
  return result;
}

// n8n input
const inputData = $input.all();

const { cart_id, product_id, quantity } = extractFields(inputData);

return [
  {
    cart_id,
    product_id,
    quantity,
  }
];
