return items.map(item => {
  const result = {
    user_id: null,
    feedback: null,
    sentiment_analysis: null
  };

  // 1. Define detection patterns
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
  const sentiments = ['positive', 'negative', 'neutral'];

  function inspectValue(val) {
    if (typeof val !== 'string') return;

    const cleanVal = val.trim();

    // Check for User ID (UUID format)
    if (uuidRegex.test(cleanVal)) {
      result.user_id = cleanVal;
    } 
    // Check for Sentiment
    else if (sentiments.includes(cleanVal.toLowerCase())) {
      result.sentiment_analysis = cleanVal.charAt(0).toUpperCase() + cleanVal.slice(1).toLowerCase();
    } 
    // Check for Feedback (If it's a longer string and not JSON)
    else if (cleanVal.length > 5 && !cleanVal.startsWith('{')) {
      // We prioritize longer text as feedback
      result.feedback = cleanVal;
    }
  }

  function findValues(obj) {
    if (!obj || typeof obj !== 'object') return;

    for (const key in obj) {
      const value = obj[key];

      // If value is a string, check if it's a nested JSON string
      if (typeof value === 'string' && value.trim().startsWith('{')) {
        try {
          findValues(JSON.parse(value));
        } catch (e) {
          inspectValue(value);
        }
      } 
      // If it's a standard object, recurse
      else if (typeof value === 'object' && value !== null) {
        findValues(value);
      } 
      // Otherwise, inspect the primitive value
      else {
        inspectValue(value);
      }
    }
  }

  findValues(item.json);

  return { json: result };
});
