/**
 * Recursively searches for 'order_id' key or a UUID pattern in any string value.
 * - Handles deep nesting, arrays, flat objects, and JSON strings.
 */

function extractOrderId(data) {
    let foundOrderId = null;

    // Regex for UUID (v4 is common): xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx
    // This pattern is general enough to catch the required format.
    const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;

    function traverse(value) {
        if (foundOrderId !== null) return;

        // Case 1: Value check - If the value is a string and matches the UUID pattern
        if (typeof value === 'string' && uuidRegex.test(value)) {
            // Found a valid-looking UUID, use it as the order_id
            foundOrderId = value;
            return;
        }

        // Case 2: JSON string → try parsing
        if (typeof value === 'string') {
            try {
                const parsed = JSON.parse(value);
                traverse(parsed);
            } catch (e) {
                // Not JSON → ignore
            }
            return;
        }

        // Case 3: Array → traverse each item
        if (Array.isArray(value)) {
            for (const item of value) {
                traverse(item);
                if (foundOrderId !== null) return;
            }
            return;
        }

        // Case 4: Object → inspect keys
        if (typeof value === 'object' && value !== null) {
            for (const key of Object.keys(value)) {

                // Priority Check: Key named 'order_id'
                if (key === 'order_id') {
                    // Ensures that even if the value is not a UUID, we take it if the key is explicitly named
                    foundOrderId = value[key];
                    return;
                }

                // Recurse to check the value of the current key
                traverse(value[key]);
                if (foundOrderId !== null) return;
            }
        }
    }

    traverse(data);
    return foundOrderId;
}

// n8n input (works for single or multiple items)
const inputData = $input.all();

// Try extracting from entire payload
const orderId = extractOrderId(inputData);

// The output structure should match the number of input items in n8n for best practice, 
// but since the goal is to find ONE order_id from the whole payload, we return a single item.
// If you need the output to be replicated for every input item, use items.map().
return [
    {
        json: {
            order_id: orderId
        }
    }
];
