/**
 * Recursively searches for `order_id` in any structure:
 * - Handles deep nesting
 * - Handles arrays
 * - Handles flat objects
 * - Handles JSON strings
 * - Treats UUID-like strings as order_id
 */

function extractOrderId(data) {
  let foundOrderId = null;

  // UUID regex pattern (v4-style, general enough)
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;

  function traverse(value) {
    if (foundOrderId !== null) return;

    // Case 1: JSON string → try parsing
    if (typeof value === 'string') {
      // Check if string looks like a UUID
      if (uuidRegex.test(value)) {
        foundOrderId = value;
        return;
      }

      try {
        const parsed = JSON.parse(value);
        traverse(parsed);
      } catch (e) {
        // Not JSON → ignore
      }
      return;
    }

    // Case 2: Array → traverse each item
    if (Array.isArray(value)) {
      for (const item of value) {
        traverse(item);
        if (foundOrderId !== null) return;
      }
      return;
    }

    // Case 3: Object → inspect keys
    if (typeof value === 'object' && value !== null) {
      for (const key of Object.keys(value)) {
        // Direct match for order_id
        if (key === 'order_id') {
          foundOrderId = value[key];
          return;
        }

        // If key might contain order ID (common keys) and value is UUID
        if (
          (key.toLowerCase().includes('order') || key.toLowerCase().includes('id')) &&
          typeof value[key] === 'string' &&
          uuidRegex.test(value[key])
        ) {
          foundOrderId = value[key];
          return;
        }

        traverse(value[key]);
        if (foundOrderId !== null) return;
      }
    }
  }

  traverse(data);
  return foundOrderId;
}

// n8n input (works for single or multiple items)
const inputData = $input.all();

// Try extracting from entire payload
const orderId = extractOrderId(inputData);

return [
  {
    order_id: orderId
  }
];

// The 'items' variable holds an array of all input items.
// Each item is an object like: { json: { ... }, ... }
// We want to process the 'json' data of each item.

// We use 'map' to iterate over all input items and transform them.
const extractedData = items.map(item => {
    // Access the data object from the current item
    const inputJson = item.json;

    // Check if the 'status' property exists
    if (inputJson && inputJson.status !== undefined) {
        // Return a new item structure containing only the extracted 'status'
        return {
            json: {
                status: inputJson.status
            }
        };
    } else {
        // If 'status' is missing, you might want to return an empty object or handle the error
        console.warn("Input item is missing the 'status' property.");
        return {
            json: {
                status: null, // Or any default value you prefer
                error: "Status property not found"
            }
        };
    }
});

// The Code node expects the final result to be returned.
return extractedData;
